import React,{useState} from 'react'
import { Link} from "react-router-dom";
import Form from "react-validator/build/form"
import Input from "react-validator/build/input"
import CheckButton from "react-validator/build/button"

import './../css/Login.css'

import Input_login from './Input_login';
import AuthService from "../services/auth_service"

import PersonIcon from '@material-ui/icons/Person';
import LockIcon from '@material-ui/icons/Lock';
import Avatar from '@material-ui/core/Avatar';




class Login extends React.Component {
    constructor(){
        super()
        this.state = {
            username:"",
            password:"",
            loading:false,
            message:"",
        }
        this.handleLogin = this.handleLogin.bind(this)
        this.onChangeUsername = this.onChangeUsername.bind(this)
        this.onChngePassword = this.onChangePassword.bind(this)

    }

    onChangeUsername(e) {
        this.setState({
            uesrname : e.target.value 
        });
    }

    onChangePassword(e) {
        this.setState({
            password: e.target.value
        })
    }

    handleLogin(e) {
        e.preventDefault()
    }

    // this place for have something

    render(){
    return (
        <div className="Login__part">

            <div className="Login__input">
                {/* <Avatar className="Login__avatar"/> */}
                <h1 className="Login__header">Sign In</h1>


                <Form 
                className=""
                 onSubmit={this.handleLogin}
                 ref={c => {
                     this.form = c;
                 }}  
                >

                <Input_login 
                Icon={PersonIcon} 
                title={"username"}
                name="username"
                value={this.state.username}
                onChange={this.onChangeUsername}
                validations={[required]}
                type={text}
                />

                <Input_login 
                    Icon={LockIcon}
                    title={"password"}
                    name='password'
                    value={this.state.password}
                    onChange={this.onChangePassword}
                    validations={[required]}
                    type={password}
                 />

                <Link to="/register"><button className="Login__button" type="submit" >Login</button></Link>

                </Form>


                <div className="Input__checkbox">
                    
                        <input type="checkbox" />
                        <h4 className="Input__rememberme">rememberme</h4>
                        <Link className="Input__forgotpass" to="https://google.com">Forgot Password</Link>
                    
                    
                </div>

              
            </div>
            <hr className="Login__line"></hr>
            <div className="Login__regis">
                <h4>Don't have a account</h4>
                <Link to="/register" >Register Here!</Link>
            </div>
        </div>

    )
    }
}


export default Login
