      //         error.message ||
      //         error.toString();

      //         this.setState({
      //           successful: false,
      //           message: resMessage
      //         })
      //     }
      //   )
      // }


    }

  return (
    <div className="list_row">
      <Form 
        onSubmit={handleCreate}
        // ref = {c => {
        //   this.form = c
        // }}
      
      >
       <div className="form-group">
         <label htmlFor="name">Name</label>
         <input className="form-control" id="name" />
       </div>
       <div className="form-group">
         <label htmlFor="email">Email address</label>
         <input
           type="email"
           className="form-control"
           id="email"
           placeholder="name@example.com"
         />
       </div>

       {/* <div> */}
           {/* <h1>{props.test1}</h1> */}
           {/* <h1>test</h1> */}
           {/* <h1>{bookingDetail2.row}</h1> */}

           <ul>
               {bookingDetail2 && 
                    bookingDetail2.map(detail => (
                        <li>
                            {detail.row}
                            {detail.column}
                        </li>
                    ))
               }
           </ul>
       {/* </div> */}
       
      {/* this part it pulls all group username  */}
       <ul className="list-group" >
            {Listofmember &&
                Listofmember.map((group, index) => (
                    //  <Link to={"/detail/" + group.username} >
                        
                        < li 
                            // className={
                            //     "list-group-item " + (index === currentIndex ? "actice" : "")
                            // }
                            >
                                  
                                <HandleChecked {...group} />
                                {group.username}
                        </li>
                    //  </Link>
                ))}
        </ul>

        <div className="form-group">
         <button className="form-control btn btn-primary" type="submit">
           Submit
         </button>
       </div>

       <CheckButton
        className=""
        style={{display:"none"}}
        // ref={c => {
        //   this.checkBtn = c
        // }}
       >

       </CheckButton>
      </Form>
    </div>
  )
}

export default CreateForm
