import React, { useState, useEffect } from 'react'

import Form from "react-validation/build/form"
import Input from "react-validation/build/input"
import CreatableSelect from 'react-select/creatable';
import { useTable } from 'react-table'

import groupBooking from '../../services/groupBooking_service'

import styled from 'styled-components'
import { Container } from './groupBookingModal/Container'    //add path to container for group right here

import '../../css/Week.css'
import data from '../../api/booking_data';
import datagroupbk from '../../api/groupBooking'
import { ArrowBackIosTwoTone, ContactSupportOutlined } from '@material-ui/icons';


const Styles = styled.div`
  padding: 1rem;
  table {
    border-spacing: 0;
    border: 1px solid black;
    tr {
      :last-child {
        td {
          border-bottom: 0;
        }
      }
    }
    th,
    td {
      margin: 0;
      padding: 0.5rem;
      border-bottom: 1px solid black;
      border-right: 1px solid black;
      :last-child {
        border-right: 0;
      }
    }
  }
  `

function Table({
    columns,
    data,
    setArr,
    arrRoot,
    day,
    arrMonday,
    arrTuesday,
    arrWednesday,
    arrThrusday,
    arrFriday,
    arrSaturday,
    arrSunday,
    setArrTotal,
}) {
    // Use the state and functions returned from useTable to build your UI
    // becareful if array has some problem, just set arr value default to [0]
    // const [arr, setArrLocal] = useState([])
    // const [arrTotalTemp, setArrTotalTemp] = useState()
    let arrTotalTemp = []
    let arrTotalSpread = []
    let arr = []
    switch (day) {
        case "Monday":
            // setArrLocal(arrMonday)
            arr=arrMonday
            break;
        case "Tuesday":
            // setArrLocal(arrTuesday)
            arr=arrTuesday
            break;
        case "Wednesday":
            // setArrLocal(arrWednesday)
            arr=arrWednesday
            break;
        case "Thrusday":
            // setArrLocal(arrThrusday)
            arr=arrThrusday
            break;
        case "Friday":
            // setArrLocal(arrFriday)
            arr=arrFriday
            break;
        case "Saturday":
            // setArrLocal(arrSaturday)
            arr=arrSaturday
            break;
        case "Sunday":
            // setArrLocal(arrSunday)
            arr=arrSunday
            break;
    }


    const bgforTime = {
        background: "white"
    }
    const bgforTrue = (cell) => {

        let x = {
            // row:cell.row.index,
            time: cell.row.values.time,
            column: cell.column.Header
        }
        for (let j in arr) {
            var bgCheck = false
            if (JSON.stringify(x) == JSON.stringify(arr[j])) {
                bgCheck = true
                break
            }
        }
        if (bgCheck) {
            return (
                {
                    background: "green"
                }
            )
        } else {
            return { background: 'white' }
        }

    }


    const bgforFalse = {
        background: "red"

    }

    const bgCell = (cell) => {
        if (cell.column.Header == "time") {
            return bgforTime
        }
        else if (cell.value == true) {
            return bgforTrue(cell)

        }
        else {
            return bgforFalse
        }


    }


    const testClick = (cell) => {
        let x = {
            //    row:cell.row.index,
            time: cell.row.values.time,
            column: cell.column.Header
        }
        var sign = false
        for (let i in arr) {
            if (JSON.stringify(x) == JSON.stringify(arr[i])) {
                let position = i
                arr.splice(position, 1)
                sign = true
                console.log("remove")
                console.log("position " + position)
                { setArr({ arr }) }

                break

            }
        }
        //    }
        if (cell.value == true && sign == false) {
            arr.push(x)
            console.log("add")

            { setArr({ arr }) }


        }
        arrTotalTemp.push(
            arrMonday,
            arrTuesday,
            arrWednesday,
            arrThrusday,
            arrFriday,
            arrSaturday,
            arrSunday,
        )

        arrTotalTemp.forEach(i => 
            i.forEach(j => arrTotalSpread.push(j))
            )
       

        console.log("this is arr")
        console.log(arr)
        console.log("this is arrRoot")
        console.log(arrRoot)
        console.log(x)
        console.log("value ")
        console.log(cell.row.values.time)

        console.log(arrMonday)
        console.log(arrTuesday)
        console.log(arrWednesday)
        console.log(arrThrusday)
        console.log(arrFriday)
        console.log(arrSaturday)
        console.log(arrSunday)

        console.log(arrTotalTemp)

        // {setArrTotal(arrTotalTemp)}
        {setArrTotal(arrTotalSpread)}

    }


    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow,
    } = useTable({
        columns,
        data,
    })
    // Render the UI for your table
    return (
        <table {...getTableProps()}
            className="Booking__table"
        >
            <thead>
                {headerGroups.map(headerGroup => (
                    <tr {...headerGroup.getHeaderGroupProps()}>
                        {headerGroup.headers.map(column => (
                            <th {...column.getHeaderProps()}>{column.render('Header')}</th>
                        ))}
                    </tr>
                ))}
            </thead>
            <tbody {...getTableBodyProps()}>
                {rows.map((row, i) => {
                    prepareRow(row)
                    return (
                        <tr {...row.getRowProps()}>
                            {row.cells.map(cell => {
                                return <td {...cell.getCellProps()}
                                    style={bgCell(cell)}
                                    onClick={() => { testClick(cell) }}
                                >
                                    {cell.render('Cell')}
                                </td>
                            })}
                        </tr>
                    )
                })}
            </tbody>
        </table>
    )

}



function Week() {

    const [day, setDay] = useState("Monday")
    const [monday, setMonday] = useState([])
    const [tuesday, setTuesday] = useState([])
    const [wednesday, setWednesday] = useState([])
    const [thrusday, setThrusday] = useState([])
    const [friday, setFriday] = useState([])
    const [saturday, setSaturday] = useState([])
    const [sunday, setSunday] = useState([])


    // const [temp, setTemp] = useState(undefined)

    const [dataTable, setDataTable] = useState([])

    const [arrRoot, setArr] = useState([0])
    const [arrTotal,setArrTotal] = useState([]) 

    const [arrTemp, setArrTemp] = useState([])
    const [arrMonday, setArrMonday] = useState([])
    const [arrTuesday, setArrTuesday] = useState([])
    const [arrWednesday, setArrWednesday] = useState([])
    const [arrThrusday, setArrThrusday] = useState([])
    const [arrFriday, setArrFriday] = useState([])
    const [arrSaturday, setArrSaturday] = useState([])
    const [arrSunday, setArrSunday] = useState([])

    // arrTotal.push(
    //     arrMonday,
    //     arrTuesday,
    //     arrWednesday,
    //     arrThrusday,
    //     arrFriday,
    //     arrSaturday,
    //     arrSunday,
    // )

    useEffect(() => {

    groupBooking.getDays().then(response => {
        // console.log(response.data)
        // console.log(response.data.status[0].status)
        setMonday(response.data.status[0])
        setTuesday(response.data.status[1])
        setWednesday(response.data.status[2])
        setThrusday(response.data.status[3])
        setFriday(response.data.status[4])
        setSaturday(response.data.status[5])
        setSunday(response.data.status[6])
