import React , {useState, useEffect}from 'react'
import ReactDom from 'react-dom'
import { Link } from "react-router-dom"
import CheckButton from "react-validation/build/button"
import Form from "react-validation/build/form"

import Memberlist from '../../services/memberlist_service'

import "../../css/CreateForm.css"
import { checkPropTypes } from 'prop-types'

  const HandleChecked = (props) => {

    const [Change,setChange] = useState(props)

    return (

      // Some problem here, it is how to send props in hooks
      <div>
        <input 
          type="checkbox"
          checked={Change.isCheck}
          onChange={() => setChange(!Change.isCheck)}
        />
      </div>
    )
  }



class CreateForm extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      Listofmember:[],
      Successful:false,
      bookingDetail2:[],
      
    }

    this.retrieveAllmember = this.retrieveAllmember.bind(this)
    this.bookingInformation = this.bookingInformation.bind(this)
    this.handleCreate = this.handleCreate.bind(this)
  }
//  const CreateForm = (props) => {
  // const [memberlist, setMemberlist] = useState({})

  // useEffect(() => {
  //   const member_list = Memberlist.member();
  //   member_list.then((response) => {
  //     setMemberlist(response.map())
  //   })
  // })

  // const [Listofmember , setListofmember] = useState([])
  // const [Successful , setSuccessful] = useState(false)
  // const [bookingDetail2, setBookingDetail] = useState([])
    
  // useEffect(() => {
  //      retrieveAllmember();
  //      bookingInformation();
  //   }, [])

    componentDidMount(){
      this.retrieveAllmember();
       this.bookingInformation();
    }


    retrieveAllmember(){
        Memberlist.getAll()
            .then(response => {
                this.setState({
                  Listofmember : response.data
                })   // data is list of name of group
                console.log(response.data)
            })
            .catch(e => {
                console.log(e)
            })
    }

    bookingInformation(){
  
    this.setState({
      bookingDetail2: this.props.bookingDetail
    })
    }

    handleCreate(e){
      e.preventDefault()
      this.form.validateAll();
     
      this.setState({
        Successful:false
      })

      if(this.checkBtn.context._errors.length === 0){
        Memberlist.createRequire(this.Listofmember)
        .then(
          response => {
            this.setState({
              message: response.data.message,
              Successful: true
            })
          },
          error => {
            const resMessage = 
            (error.response && 
              error.response.data &&
              error.response.data.message) ||
              error.message ||
              error.toString();

              this.setState({
                successful: false,
                message: resMessage
              })
          }
        )
      }

      

    }
    render(){
  return (
    <div className="list_row">
      <Form 
        onSubmit={this.handleCreate}
        ref = {c => {
          this.form = c
        }}
      
      >
       <div className="form-group">
         <label htmlFor="name">Name</label>
         <input className="form-control" id="name" />
       </div>
       <div className="form-group">
         <label htmlFor="email">Email address</label>
         <input
           type="email"
           className="form-control"
           id="email"
           placeholder="name@example.com"
         />
       </div>

           <ul>
               {this.bookingDetail2 && 
                    this.bookingDetail2.map(detail => (
                        <li>
                            {/* {detail.time}
                            {detail.column} */}
                          {detail.time}  {detail.column}
                        </li>
                    ))
               }
           </ul>
       
        <div className="form-group">
         <button className="form-control btn btn-primary" type="submit">
           Submit
         </button>
       </div>

       <CheckButton
        className=""
        style={{display:"none"}}
        ref={c => {
          this.checkBtn = c
        }}
       >

       </CheckButton>
      </Form>
    </div>
  )
      }
}

export default CreateForm
