import React from 'react'
import './css/Afterbooking.css'
import SetTableValue from './SetTableValue'
import Row_value from './DynamicTable'

class Afterbooking extends React.Component {
    constructor(){
        super()
        this.state = ({
            Row1 : Row_value,
            bgColor:"",
            count:1,
            test4 : true
            
        })
        this.handleClick = this.handleClick.bind(this)
        this.bgChange =  this.bgChange.bind(this)
       
    }

    renderTable(){
        let header = Object.keys(this.state.Row1[0])
        return header.map((key, index) => {
             return <th key={index}>{key.toUpperCase}</th>
        })
    }

    handleClick(){
        return this.state.Row1.map((test) => {
            const { id, time, court1, court2, court3, court4, court5, court6, court7, court8, court9} = test
            return(
                <tr>
                    <th >{time}</th>
                    <td key={id} style={{backgroundColor:this.state.bgColor}} onClick={() => this.bgChange(id)}>{id} {court1} </td>
                    <td key={id} style={{backgroundColor:this.state.bgColor}} onClick={() => this.bgChange(id)}>{id} {court2}</td>
                    <td key={id} style={{backgroundColor:this.state.bgColor}} onClick={() => this.bgChange(id)}>{id} {court3}</td>
                    <td key={id} style={{backgroundColor:this.state.bgColor}} onClick={() => this.bgChange(id)}>{id} {court4}</td>
                    <td key={id} style={{backgroundColor:this.state.bgColor}} onClick={() => this.bgChange(id)}>{id} {court5}</td>
                    <td key={id} style={{backgroundColor:this.state.bgColor}} onClick={() => this.bgChange(id)}>{id} {court6}</td>
                    <td key={id} style={{backgroundColor:this.state.bgColor}} onClick={() => this.bgChange(id)}>{id} {court7}</td>
                    <td key={id} style={{backgroundColor:this.state.bgColor}} onClick={() => this.bgChange(id)}>{id} {court8}</td>
                    <td key={id} style={{backgroundColor:this.state.bgColor}} onClick={() => this.bgChange(id)}>{id} {court9}</td>
                </tr>
            )
        })
    }

    check() {
        console.log(alert(1))
    }

    bgChange(id){ 
        this.state.test4 ? this.setState({bgColor : 'lime', test4 : false}) : this.setState({bgColor : '', test4 : true}) 
        console.log(alert(id))
    
}
 

    // bgChange(id){
    //     if(this.state.test4 === true){
    //     this.setState({
            
    //         bgColor : 'lime',
    //         test4 : false
    //         })
    //     }
        
    //     else{
    //         this.setState({bgColor:'',test4:true})
    //     }
        
        
    
    // }
   
        render(){
    return (
     
        <div className="after__booking">
        
            <table className="booking__table">
                <tbody>  
                    <tr>{this.renderTable()}</tr>
                        {this.handleClick()}
                
            
            
                </tbody>
            </table>

            <form className="Booking__submit" method='POST' action=''>
                <button  className='Cancle__button' type='submit' onClick = { () => console.log(alert(1))}>cancle</button>
                <button  className='Submit__button' type='submit'>submit</button>
            </form>
        </div>
    )
}
}
export default Afterbooking
