import React, { useState, useEffect } from 'react'

import Form from "react-validation/build/form"
import Input from "react-validation/build/input"
import CreatableSelect from 'react-select/creatable';

import groupBooking from '../../services/groupBooking_service'


// import Form from "react-validation/build/form"

import '../../css/Week.css'

function Week() {

    const [day, setDay] = useState("Monday")
    const [temp, setTemp] = useState(undefined)

    useEffect(() => {

    }, [])

    const options = [
        { value: 'Monday', label: 'Monday' },
        { value: 'Tuesday', label: 'Tuesday' },
        { value: 'Wednesday', label: 'Wednesday' },
        { value: 'Thursday', label: 'Thursday' },
        { value: 'Friday', label: 'Friday' },
        { value: "Saturday", label: 'Saturday' },
        { value: "Sunday", label: 'Sunday' }

    ];
    const defaultOption = options[0];

    const handleOption = (newValue) => {
        if (newValue == null) { return setDay("") }
        setDay(newValue)
        groupBooking.getDay(newValue.value)
        // console.log(newValue.value)
    }

    const columns = React.useMemo(
        () =>
            [
                {
                    Header: "Numbers",
                    accessor: "number"
                },
                {
                    Header: "Members",
                    accessor: "firstname"
                },
            ]
        ,
        []
    )

    // const data = React.useMemo(() => test, [])
    const data = members
    console.log("this is not Header")
    console.log(data)
    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow,
    } = useTable({ columns, data })


    return (
        <div className="Week">
            <Form>


                <CreatableSelect
                    className="week__dropdown"
                    isClearable
                    onChange={handleOption}
                    // onInputChange={}
                    options={options}
                // value={day}


                />

            </Form>

            <h1>{temp}</h1>
            <h1>this is for Group booking page</h1>
            <h1>{day.value}</h1>

            <div>
                <table {...getTableProps()} style={{ border: 'solid 1px blue' }}>
                    <thead>
                        {headerGroups.map(headerGroup => (
                            <tr {...headerGroup.getHeaderGroupProps()}>
                                {headerGroup.headers.map(column => (
                                    <th
                                        {...column.getHeaderProps()}
                                        style={{
                                            borderBottom: 'solid 3px red',
                                            background: 'aliceblue',
                                            color: 'black',
                                            fontWeight: 'bold',
                                        }}
                                    >
                                        {column.render('Header')}
                                    </th>
                                ))}
                            </tr>
                        ))}
                    </thead>
                    <tbody {...getTableBodyProps()}>
                        {rows.map(row => {
                            prepareRow(row)
                            return (
                                <tr {...row.getRowProps()}>
                                    {row.cells.map(cell => {
                                        return (
                                            <td
                                                {...cell.getCellProps()}
                                                style={{
                                                    padding: '10px',
                                                    border: 'solid 1px gray',
                                                    background: 'papayawhip',
                                                }}
                                            >
                                                {
                                                    cell.render('Cell')
                                                }
                                            </td>
                                        )
                                    })}
                                </tr>
                            )
                        })}
                    </tbody>
                </table>
                {/* <h1>test</h1> */}
            </div>
        </div>
    )
}

export default Week
