      //       if(response.data.success == false){
      //         window.alert("Sorry, please check stage again")
      //         window.location.reload()}
      //       this.setState({
      //         message: response.data.message,
      //         uuid:response.data.bookingid, // change this line 
      //         price:response.data.price,
      //         isSubmit : true,

      //       })
      //     },
      //     error => {
      //       const resMessage = 
      //       (error.response && 
      //         error.response.data &&
      //         error.response.data.message) ||
      //         error.message ||
      //         error.toString();

      //         this.setState({
      //           message: resMessage
      //         })
      //     }
      //   )
      // }

      

    }

    bookingVerfify(e){
      e.preventDefault()
      this.form.validateAll();
     
      // this.setState({
      //   Successful:false
      // })

      // if(this.checkBtn.context._errors.length === 0){
      //   BookingService.BookingVerify(
      //     this.state.uuid,
      //   )
      //   .then(
      //     response => {
      //       this.setState({
      //         message: response.data.message,
      //         // isSuccess:true,
      //         // Successful: true
      //       })
      //       window.location.reload()
      //       window.alert("Your booking is Successful")
      //     },
      //     error => {
      //       const resMessage = 
      //       (error.response && 
      //         error.response.data &&
      //         error.response.data.message) ||
      //         error.message ||
      //         error.toString();

      //         this.setState({
      //           // successful: false,
      //           message: resMessage
      //         })
      //     }
      //   )
      // }

      

    }

    render(){
      console.log(this.state.uuid)
      const { member, isSubmit } = this.state
  return (
   <div>
     { isSubmit ? 
     // something
    //  <h1>test</h1>
     <div>
     <Form
      onSubmit={this.bookingVerfify}
      ref = {c => {
        this.form = c
      }}

      >
        {/* I think we should should some qr code for tranfers the money */}
        <h1>Please sends your receipt to the admin through line</h1>
        <h2>Thank you</h2>

        <h1>Total price</h1>
        <h1>{this.state.price.normal_price}</h1>
        <h1>{this.state.price.discount_time}</h1>
        <h1>{this.state.price.discouht_member}</h1>

        <div className="form-group">
         <button className="form-control btn btn-primary" type="submit">
           Verify
         </button>
       </div>

       <CheckButton
        className=""
        style={{display:"none"}}
        ref={c => {
          this.checkBtn = c
        }}
       >

       </CheckButton>

      </Form>

      </div>
     
     :
    <div className="list_row">
      <Form 
        onSubmit={this.handleCreate}
        ref = {c => {
          this.form = c
        }}
      
      >
      { !member ? <div>
       <div className="form-group">
         <label className="Header__label">Name</label>
         <Input 
          className="form-control"
          id="name" 
          value={this.state.name}
          onChange={this.onChangename}
          // validations={[name]}
          />

       </div>

       <div className="form-group">
         <label className="Header__label">Email address</label>
         <Input
           type="text"
           className="form-control"
           id="email"
           placeholder="name@example.com"
           value={this.state.email}
           onChange={this.onChangeemail}
          //  validations= {[vemail]}
         />
       </div>

       <div className="form-group">
          <label className="Header__label">Phone number</label>
          <Input
            className="form-control"
            id="phone"
            placeholder="012-345-6789"
            value={this.state.phone}
            onChange={this.onChangephone}
            // validations={[phone]}
          />
       </div>
       </div>
       : <h1>Booking Details</h1>
      }
           <ul
            className ="ul__booking"
           >
               {this.state.bookingDetail2 && 
                    this.state.bookingDetail2.map(detail => (
                        <li
                          className = "li__booking"
                        >
                            {/* {detail.time}
                            {detail.column} */}
                          {detail.time}  {detail.column}
                        </li>
                    ))
               }
           </ul>
       
        <div className="form-group">
         <button className="Booking__submit" type="submit">
           Submit
         </button>
       </div>

       <CheckButton
        className=""
        style={{display:"none"}}
        ref={c => {
          this.checkBtn = c
        }}
       >

       </CheckButton>
      </Form>
    </div>
    }

    </div>
      
  )
      }
}

