import axios from 'axios';

axios.defaults.withCredentials = true;

const API_URL = "http://localhost:8000/";

class Notification{

    get(){
        return axios
        .get(API_URL + 'request/')
        .then(response => {
            return response
        })
    }

    accept(id){
        return axios
        .post(API_URL + 'request/',{
            id
        })
    }
}

export default new Notification()
