import React , {useState, useEffect}from 'react'

import Data from '../api/group__members'
import GroupList from "../services/groupservices"

import { useTable } from 'react-table'

import styled from 'styled-components'

function Headertable({ dataHeader, authority2}) {
    const [dataApi, setApi] = useState([])
    const [authority, setAuthority] = useState(undefined)

    const group = GroupList.getCurrentGroup()


const handleDelete = (id) => {
  GroupList.deleteMember(group,id)
  .then(response => {
    if(response.data.msg == "delete member successful")
    window.alert("Delete successfully");
    window.location.reload();
  }

  )
}

const Styles = styled.div`
padding: 1rem;
table {
  border-spacing: 0;
  border: 1px solid black;
  tr {
    :last-child {
      td {
        border-bottom: 0;
      }
    }
  }
  th ,
  td {
    margin: 0;
    padding: 1rem;
    border-bottom: 1px solid black;
    border-right: 1px solid black;
    :last-child {
      border-right: 0;
    }
  }
}
`

    const columns = React.useMemo(
        () =>
          [
            {Header:"Members",
          columns:[
                {
              Header: "Numbers",
              accessor: "number"
            },
            {
              Header: "Members",
              accessor: "firstname"
            },
            {
              Header: "Delete",
              accessor: "delete"
            }
          ]
        }
          ]
        ,
        []
      )
    

      const data =dataHeader
      console.log("this is data from Header", authority2)
          console.log(dataHeader)
    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow,
      } = useTable({ columns, data })
    
    return (
        <div>
          <Styles>
            <table {...getTableProps()} style={{ border: 'solid 1px blue' }}>
          <thead>
            {headerGroups.map(headerGroup => (
              <tr {...headerGroup.getHeaderGroupProps()}>
                {headerGroup.headers.map(column => (
                  <th
                    {...column.getHeaderProps()}
                    style={{
                      borderBottom: 'solid 3px red',
                      background: 'aliceblue',
                      color: 'black',
                      fontWeight: 'bold',
                    }}
                  >
                    {column.render('Header')}
                  </th>
                ))}
              </tr>
            ))}
          </thead>
          <tbody {...getTableBodyProps()}>
            {rows.map(row => {
              prepareRow(row)
              return (
                <tr {...row.getRowProps()}>
                  {row.cells.map(cell => {
                    return (
                      <td
                        {...cell.getCellProps()}
                        style={{
                          padding: '10px',
                          border: 'solid 1px gray',
                          background: 'papayawhip',
                        }}
                      >
                        { typeof (cell.value) == "boolean" && authority2 == 0
                          ?
                          <div>
                            <button onClick={() => handleDelete(cell.row.original.id)}>Delete</button>
                            {/* {setColumn(authority)} */}
                          </div>
                          :
                          cell.render('Cell')
                        }
                      </td>
                    )
                  })}
                </tr>
              )
            })}
          </tbody>
        </table>
        </Styles>
        </div>
    )
}

export default Headertable
