import React from 'react'
import './css/Afterbooking.css'
import SetTableValue from './SetTableValue'


import { AddAlert } from '@material-ui/icons'
import { green } from '@material-ui/core/colors'
import Row_value from './DynamicTable'

// import './Booking__function.js';

class Afterbooking extends React.Component {
    constructor(){
        super()
        this.state = ({
            Row1 : Row_value,
            bgColor : "white"
        })
        this.handleClick = this.handleClick.bind(this)
        this.bgChange =  this.bgChange.bind(this)
       
    }

    renderTable(){
        let header = Object.keys(this.state.Row1[0])
        return header.map((key, index) => {
             return <th key={index}>{key.toUpperCase}</th>
        })
    }


    
    handleClick(){
        return this.state.Row1.map((test) => {
            const { time, court1, court2, court3, court4, court5, court6, court7, court8, court9} = test
            return(
                <tr>
                    <th>{time}</th>
        <td style = {{backgroundColor:this.state.bgColor}} onClick={this.bgChange()}>{court1}</td>
                    <td>{court2}</td>
                    <td>{court3}</td>
                    <td>{court4}</td>
                    <td>{court5}</td>
                    <td>{court6}</td>
                    <td>{court7}</td>
                    <td>{court8}</td>
                    <td>{court9}</td>
                </tr>
            )
        })
    }

    // bgChange(){
    //    this.setState(prevbg => {
    //        const bgColorChange = 'red'
           
    //      return{
    //          var : prevbg.bgColor
    //      }
    //     })
    // }
   
    render(){

      
    return (
     
        <div className="after__booking">
        
            <table className="booking__table">
                <tbody>
                    {/* <tr>
                        <th></th>
                        <th>class1</th>
                        <th>class2</th>
                        <th>class3</th>
                        <th>class4</th>
                        <th>class5</th>
                        <th>class6</th>
                        <th>class7</th>
                        <th>class8</th>
                        <th>class9</th>

                    </tr> */}
                    <tr>{this.renderTable()}</tr>
                        {this.handleClick()}
                
            
            
                </tbody>
            </table>



            <form className="Booking__submit" method='POST' action=''>
                <button  className='Cancle__button' type='submit' onClick = { () => console.log(alert(1))}>cancle</button>
                <button  className='Submit__button' type='submit'>submit</button>
            </form>
        </div>
    )
}
}
export default Afterbooking
