    renderTable(){
        let header = Object.keys(this.state.Row1[0])
        return header.map((key, index) => {
             return <th key={index}>{key.toUpperCase}</th>
        })
    }

    handleClick(){
    
        return this.state.Row1.map((test) => {
            const { id, time, court1, court2, court3, court4, court5, court6, court7, court8, court9, backgroundColor:{bgColor1,bgColor2
            ,bgColor3,bgColor4,bgColor5,bgColor6,bgColor7,bgColor8,bgColor9}} = test
            return(
                <tr>
                    <th >{time}</th>
                    <td key={id} style={{backgroundColor:bgColor1}} onClick={() => this.bgChange(id)}>{id} {court1} </td>
                    <td key={id} style={{backgroundColor:bgColor2}} onClick={() => this.bgChange(id)}>{id} {court2}</td>
                    <td key={id} style={{backgroundColor:bgColor3}} onClick={() => this.bgChange(id)}>{id} {court3}</td>
