                    <td key={id} style={{backgroundColor:bgColor8}} onClick={() => this.bgChange(id)}>{id} {court8}</td>
                    <td key={id} style={{backgroundColor:bgColor9}} onClick={() => this.bgChange(id)}>{id} {court9}</td>
                </tr>
            )
        })
    }

    

    bgChange(id){ 
     

        if (id === 1){this.state.test4 ? this.setState({bgColor1 : 'lime', test4 : false}) : this.setState({bgColor1 : '', test4 : true})} 
