import React, {useState , useEffect} from 'react'
import GroupList from "../services/groupservices"

import {useTable}  from 'react-table'

import '../css/Groupdetail.css'

import Data from '../api/group__members'
import button from 'react-validation/build/button'

function Groupdetail(props) {

   const [CurrentGroup, setCurrentGroup] = useState(undefined)  // match with table
   const [presentGroup, setPresentGroup] = useState(undefined)
   const [members, setMembers] = useState([])
   const [authority, setAuthority] = useState(0)
   const [role, setRole] = useState(undefined)
   const [dataTerm, setData] = useState([])
   const [isPrivacy , setPrivacy] = useState(false)
   const [joingroup, setJoin] = useState(undefined)

    const getGroup = name => {
        GroupList.get(name)
            .then(response => {
                setCurrentGroup(response.data)
                console.log(response.data)
            })
            .catch(e => {
                console.log(e)
            })
    }
    useEffect(() => {
     
      const group = GroupList.getCurrentGroup()
       setPresentGroup(
        GroupList.getCurrentGroup()
    )
        
        GroupList.getMembers(group)
        .then(response =>{
          console.log(response.data.member)
          handleMembers(response.data.member)
          authorityCheck(response.data.role)
          setJoin(response.data.group_name)
        })
        // set members to data variable
    
        // handleMembers()

        // authorityCheck(role)

    }, [])
    // console.log("this is members")
    // console.log(members)
    console.log("this is dataTerm")
    console.log(dataTerm)
    console.log(Data)

    const handleMembers = (members) => {
      let count = 0
      const dataTemp = []
      for(let i in members){
        console.log("i information")
        console.log(members[i])
        dataTemp.push({
          sequence:count,
          member:members[i]
        })
      count++
      }
      return setData(
        dataTemp
      )
    }

    const authorityCheck = (role) => {
      if (role == "header"){
        setAuthority(0)
      }else if( role == "member"){
        setAuthority(1)
      }else if (role == "wating"){
        setAuthority(2)
      }else{
        setAuthority(3) //this case status is free
      }
    }

    // ***************************************************************************
    // Services is here

    const handleJoin = () => {
      alert(1)
      // GroupList.joinGroup(joingroup)
      //  console.log("something that want to join")
    }

    const handleLeave = () => {

    }

    const handleDelete = (cell) => {

    }

    const handleCancel = () => {

    }

    const handlePrivacy = (status) => {
      if(status){
        //send request to server that want to set group to public
      }else{
        //send request to server that want to set group to private
      }
      setPrivacy(!status)
    }
// ***************************************************************************
    const header = () => {
      return ( 
            // <button
            //   className="header__privacy"
            //   onClcik = {() => handlePrivacy()}
            //   >on</button>
        isPrivacy ? 
         <button 
          className="privacy__on" 
          onClick = {() => {handlePrivacy(false)}}
          >
            ON
          </button>
          :
          <button 
          className="privacy__off" 
          onClick = {() => {handlePrivacy(true)}}
          >
            OFF
          </button>
              
      )

    }

    const member = () => {
      return <button 
              className="member__leave"
              // onClick = {() => alert("leave")}
              >
                Leave
              </button>
    }

    const free = () => {
      return <button
              className="free__join"
              onClcik = {() => handleJoin()}
              // onClick = {console.log("test")}
              >
                Join
              </button>
      // return <h1>testtesttetstet</h1>
    }

    const wating = () => {
      return <button
              className="wating__cancel"
              onClick = {() => handleCancel()}
              // onClick = {() => alert("cancel")}

              >
                Cancel
               </button>
    }

    const handleRole = (authority) => {
      switch (authority) {
        case 0:
          // return console.log("this is header")
          return header();
        case 1:
          return member(); 
        case 2:
          return wating();
        case 3:
          return free();
        default:
          break;
      }
    }

    const columns = React.useMemo(
        () => [
            {
                Header: "Numbers",
                accessor:"sequence"
            },
            {
                Header:"Members",
                accessor:"member"
            }
        ],
        []
    )
    
    const data  = React.useMemo( () => Data,[])

    
   const {
     getTableProps,
     getTableBodyProps,
     headerGroups,
     rows,
     prepareRow,
   } = useTable({ columns, data })

    return (
        <div className="group__detail">
            {/* fetch all group data >> member , payment  */}
            {/* follow makedata in showgroup by use reat-table */}
            <h1><p>This page intended to show the detail of each group about 
                user and groups information
                </p>
            </h1>
            {/* <h1>{props.nameOfGroup}</h1> */}
            <h1>{presentGroup}</h1>
            <h1>ok</h1>

                 <table {...getTableProps()} style={{ border: 'solid 1px blue' }}>
       <thead>
         {headerGroups.map(headerGroup => (
           <tr {...headerGroup.getHeaderGroupProps()}>
             {headerGroup.headers.map(column => (
               <th
                 {...column.getHeaderProps()}
                 style={{
                   borderBottom: 'solid 3px red',
                   background: 'aliceblue',
                   color: 'black',
                   fontWeight: 'bold',
                 }}
               >
                 {column.render('Header')}
               </th>
             ))}
           </tr>
         ))}
       </thead>
       <tbody {...getTableBodyProps()}>
         {rows.map(row => {
           prepareRow(row)
           return (
             <tr {...row.getRowProps()}>
               {row.cells.map(cell => {
                 return (
                   <td
                     {...cell.getCellProps()}
                     style={{
                       padding: '10px',
                       border: 'solid 1px gray',
                       background: 'papayawhip',
                     }}
                   >
                     {cell.render('Cell')}
                   </td>
                 )
               })}
             </tr>
           )
         })}
       </tbody>
     </table>

      <div className="role_checks">
         {handleRole(authority)}
         <h1>{authority}</h1>

         {/* if({authority} == 0){
         {isPrivacy} ? 
         <button 
          className="privacy__on" 
          onClick = {() => {handlePrivacy(false)}}
          >
            ON
          </button>
          :
          <button 
          className="privacy__off" 
          onClick = {() => {handlePrivacy(true)}}
          >
            OFF
          </button>
          
          } */}
      </div>

    {/* { authrority ?
      
    //  CHeck Is this Header or not
     <div>
     <button 
                type="submit"
                className="Accept__member"
            >
               Accept 
            </button>
            <button
                type="submit"
                className="Delete__member"
            >
                Delete   
            </button>
     </div>
    //  this is for member
     :
        <div>
            <button 
                type="submit"
                className="Join__group"
                onClick = {() => handleJoin()}
            >
                Join
            </button>
            <button
                type="submit"
                className="Leave__group"
                onClick = {() => handleLeave()}
            >
                Leave
            </button>
        </div>
}  */}



        </div>
    )
}

export default Groupdetail
