import React , {useState, useEffect}from 'react'

import Data from '../api/group__members'
import GroupList from "../services/groupservices"

import { useTable } from 'react-table'

function Headertable({authority, dataHeader}) {
    const [dataApi, setApi] = useState([])
    // const [authority, setAuthority] = useState(props)

useEffect(() => {
    GroupList.getMembers()
      .then(response => {

        setApi(response.data.detail.member)


      })
}, [])

    const columns = React.useMemo(
        () =>
          [
            // {
            //   Header: "Numbers",
            //   accessor: "sequence"
            // },
            // {
            //   Header: "Members",
            //   accessor: "member"
            // },
            // {
            //   Header: "Delete",
            //   accessor: "manage"
            // }
                {
              Header: "Numbers",
              accessor: "id"
            },
            {
              Header: "Members",
              accessor: "firstname"
            },
            {
              Header: "Delete",
              accessor: "delete"
            }
          ]
        ,
        []
      )
    
      // const data = React.useMemo(() => dataHeader, [])
      const data = dataApi
      console.log("this is data", dataApi)

    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow,
      } = useTable({ columns, data })
    
    return (
        <div>
            <table {...getTableProps()} style={{ border: 'solid 1px blue' }}>
          <thead>
            {headerGroups.map(headerGroup => (
              <tr {...headerGroup.getHeaderGroupProps()}>
                {headerGroup.headers.map(column => (
                  <th
                    {...column.getHeaderProps()}
                    style={{
                      borderBottom: 'solid 3px red',
                      background: 'aliceblue',
                      color: 'black',
                      fontWeight: 'bold',
                    }}
                  >
                    {column.render('Header')}
                  </th>
                ))}
              </tr>
            ))}
          </thead>
          <tbody {...getTableBodyProps()}>
            {rows.map(row => {
              prepareRow(row)
              return (
                <tr {...row.getRowProps()}>
                  {row.cells.map(cell => {
                    return (
                      <td
                        {...cell.getCellProps()}
                        style={{
                          padding: '10px',
                          border: 'solid 1px gray',
                          background: 'papayawhip',
                        }}
                      >
                        { typeof (cell.value) == "boolean" && authority == 0
                          ?
                          <div>
                            <button onClick={() => alert(cell.value)}>test</button>
                            {/* {setColumn(authority)} */}
                          </div>
                          :
                          cell.render('Cell')
                        }
                      </td>
                    )
                  })}
                </tr>
              )
            })}
          </tbody>
        </table>
        </div>
    )
}

export default Headertable
